use dep::aztec::macros::aztec;

#[aztec]
pub contract AztlanNFTProfile {
    use dep::aztec::macros::{functions::{public}, storage::storage};
    use dep::aztec::prelude::{AztecAddress, Map, PublicMutable};

    #[storage]
    struct Storage<Context> {
        // Username mapping (untuk cek unik)
        username_hash_to_address: Map<Field, PublicMutable<AztecAddress, Context>, Context>,

        // Cek apakah address sudah mint
        has_minted: Map<AztecAddress, PublicMutable<bool, Context>, Context>,

        // NFT Token ID per address
        token_id_of: Map<AztecAddress, PublicMutable<Field, Context>, Context>,

        // IPFS URI untuk NFT image & metadata
        token_uri: Map<AztecAddress, PublicMutable<Field, Context>, Context>,

        // Stats
        total_minted: PublicMutable<Field, Context>,
        next_token_id: PublicMutable<Field, Context>,
    }

    #[public]
    fn constructor() {
        storage.total_minted.write(0);
        storage.next_token_id.write(1);
    }

    #[public]
    fn mint_profile(
        username_hash: Field,
        ipfs_uri_hash: Field  // Hash dari IPFS URI
    ) {
        let caller = context.msg_sender();

        // Validasi 1: Address belum pernah mint
        let already_minted = storage.has_minted.at(caller).read();
        assert(!already_minted, "Address already minted profile");

        // Validasi 2: Username unik
        let username_owner = storage.username_hash_to_address.at(username_hash).read();
        assert(username_owner == AztecAddress::zero(), "Username already taken");

        // Get token ID
        let token_id = storage.next_token_id.read();

        // Set data
        storage.username_hash_to_address.at(username_hash).write(caller);
        storage.has_minted.at(caller).write(true);
        storage.token_id_of.at(caller).write(token_id);
        storage.token_uri.at(caller).write(ipfs_uri_hash);

        // Update counters
        storage.total_minted.write(storage.total_minted.read() + 1);
        storage.next_token_id.write(token_id + 1);
    }

    #[public]
    fn update_token_uri(new_ipfs_uri_hash: Field) {
        let caller = context.msg_sender();

        // Check user has profile
        let has_profile = storage.has_minted.at(caller).read();
        assert(has_profile, "No profile found");

        // Update URI
        storage.token_uri.at(caller).write(new_ipfs_uri_hash);
    }

    // VIEW FUNCTIONS

    #[public]
    fn get_profile_by_address(user: AztecAddress) -> bool {
        storage.has_minted.at(user).read()
    }

    #[public]
    fn get_token_id(user: AztecAddress) -> Field {
        storage.token_id_of.at(user).read()
    }

    #[public]
    fn get_token_uri(user: AztecAddress) -> Field {
        storage.token_uri.at(user).read()
    }

    #[public]
    fn get_address_by_username(username_hash: Field) -> AztecAddress {
        storage.username_hash_to_address.at(username_hash).read()
    }

    #[public]
    fn is_username_taken(username_hash: Field) -> bool {
        storage.username_hash_to_address.at(username_hash).read() != AztecAddress::zero()
    }

    #[public]
    fn get_total_minted() -> Field {
        storage.total_minted.read()
    }

    #[public]
    fn has_profile(user: AztecAddress) -> bool {
        storage.has_minted.at(user).read()
    }
}

