contract AztlanProfileV3 {
    use dep::aztec::macros::{storage::storage, functions::{initializer, public}};
    use dep::aztec::prelude::{AztecAddress, PublicMutable, PublicImmutable};

    #[storage]
    struct Storage<Context> {
        total_supply: PublicMutable<Field, Context>,
        owner: PublicImmutable<AztecAddress, Context>,
    }

    #[public]
    #[initializer]
    fn constructor(owner: AztecAddress) {
        storage.owner.initialize(owner);
        storage.total_supply.write(0);
    }
}

